import { Component } from 'react'
import Link from 'next/link'

class NewsAdd extends Component {

    constructor(props) {
        super(props);
        this.state = {
        };
    }

    render () {
      return (
          <div className="container">
            <div className="header">Admin > News<div className="logout_but">Log out</div></div>
            <div className="clear"></div>
            <div className="content_box">
            </div>
                <style jsx global>{`
                    html {
                        height: 100%;
                        font-family:Gotham, "Helvetica Neue", Helvetica, Arial, sans-serif;
                        font-size:15px;
                        line-height:17px;
                        color:#333;
                    }
                    body{
                        margin:0px;
                        padding:0px;
                        width:100%; 
                        height:100%;
                    }
                    .clear{
                        clear:both;
                    }
                `}</style>
                <style jsx>{`
                    .container{
                        height:100%;
                    }
                    .content_box{
                        height:calc(100% - 60px);
                        padding:8px;
                    }
                    .header{
                        height:28px;
                        padding:8px;
                        background-color:#006; 
                        color:#FFF;
                        line-height:28px;
                        font-weight:bold;
                    }
                    .content_box{
                        height:calc(100% - 60px);
                        padding:8px;
                    }
                    .logout_but{
                        float:right;
                        padding:0px 12px 0px 12px;
                        color:#FFF;
                        cursor:pointer;
                        font-weight:normal;
                    }
                    .add_but{
                        float:right;
                        padding:4px 12px 4px 12px;
                        background-color:#006; 
                        color:#FFF;
                        border-radius:8px;
                        cursor:pointer;
                        margin-left:12px;
                    }
                    .myselect{
                        font-size:15px;
                        border:2px solid #d3d3d5;
                        background-color:#f3f3f5; 
                        border-radius:0px; 
                        -webkit-border-radius:0px; 
                        appearance:none; 
                        -webkit-appearance: none; 
                        outline:none;
                        position: relative;
                        text-align:left;
                        z-index: 99;
                        line-height:20px;
                        padding:0px 36px 0px 8px;
                        background:url(/static/images/select_arrow.png) no-repeat right #fff;
                    }
                `}</style>
            </div>
      );
    }
}

export default NewsAdd
