import { Component } from 'react'
import { withRouter } from 'next/router';
import Link from 'next/link'
import Login from '../pages/admin/login'

class Admin extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: 0
        };
    };

    getContent = (query) => {
        if (localStorage) {
            const data = localStorage.getItem('_action');
            if(data) {
                if(data == 'news_list'){
                    return (<div></div>);
                }
            }else{
                return (<Login/>); 
            }
        }else{
            return (<Login/>); 
        }
    }

    render() {
      return (
          <div className="container">
            <div className="header">Admin</div>
          {this.getContent(this.props.router.query)}
        <style jsx global>{`
            html {
                height: 100%;
                font-family:Gotham, "Helvetica Neue", Helvetica, Arial, sans-serif;
                font-size:15px;
                line-height:17px;
                color:#333;
            }
            body{
                margin:0px;
                padding:0px;
                width:100%; 
                height:100%;
            }
            .container{
                height:100%;
            }
            .header{
                height:28px;
                padding:8px;
                background-color:#006; 
                color:#FFF;
                line-height:28px;
                font-weight:bold;
            }
        `}</style>
          </div>
      );
    }
}

export default withRouter(Admin)
