import { Component } from 'react';
import jsCookie from 'js-cookie';

class NewsList extends Component {

    constructor(props) {
        super(props);
        this.state = {
        };
        this.addNewsClick = function(){
            jsCookie.set('action', 'add_news');
            var action = jsCookie.get('action');
            console.log('action2 = ' + action);
        }
    };
    /*
    addNewsClick(){
        jsCookie.set('action', 'add_news');
        var action = jsCookie.get('action');
        console.log('action2 = ' + action);
         //window.location.href = '/admin';
    };
    */

    render () {
      return (
            <div className="content_box">
                <div className="table_header">
                    <div className="filter_box">
                      <select className="myselect" name="date_filter">
                        <option value="0">All</option>
                      </select>
                    </div>
                    <a href={this.addNewsClick()} className="add_but">+ Add News</a>
                    <div className="clear"></div>
                </div>
                <div className="clear"></div>
                <style jsx>{`
                    .content_box{
                        height:calc(100% - 60px);
                        padding:8px;
                    }
                    .add_but{
                        float:right;
                        padding:4px 12px 4px 12px;
                        background-color:#006; 
                        color:#FFF;
                        border-radius:8px;
                        cursor:pointer;
                        margin-left:12px;
                    }
                    .table_header{
                        float:left;
                    }
                    .filter_box{
                        float:left;
                    }
                    .myselect{
                        font-size:15px;
                        border:2px solid #d3d3d5;
                        background-color:#f3f3f5; 
                        border-radius:0px; 
                        -webkit-border-radius:0px; 
                        appearance:none; 
                        -webkit-appearance: none; 
                        outline:none;
                        position: relative;
                        text-align:left;
                        z-index: 99;
                        line-height:20px;
                        padding:0px 36px 0px 8px;
                        background:url(/static/images/select_arrow.png) no-repeat right #fff;
                    }
                `}</style>
            </div>
      );
    }
}

export default NewsList
