import { Component } from 'react'
import Link from 'next/link'

class Admin extends Component {

    constructor(props) {
        super(props);
        this.state = {
            user: '',
            pass: ''
        };
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    async loginClick(){
        //console.log(this.state);

        try {
          const response = await window.fetch('http://localhost:3000/admin', {
            method: 'post',
            body: JSON.stringify(this.state),
            headers: { 'Content-Type': 'application/json' }
          })

          if (response.ok) {
            const { token } = await response.json()
            /*
            const loginOptions = {
              token,
              cookieOptions: { expires: 1 },
              callback: () => Router.push('/users')
            }
            login(loginOptions)
            */
          } else {
            console.log('Login failed.')
          }
        } catch (error) {
          console.log('Implementation or Network error.')
        }
    }

    render () {
      return (
          <div className="container">
            <div className="header">Admin</div>
            <div className="content_box">
                <form onSubmit={e => {
                        e.preventDefault()
                        this.loginClick()
                      }}>
                    <table border="0" align="center" cellPadding="8" cellSpacing="0">
                      <tbody>
                      <tr>
                        <td align="center" valign="top">
                            <input 
                                id="user" 
                                name="user" 
                                type="text" 
                                className="login_tf" 
                                maxLength="16"
                                value={this.state.name}
                                onChange={e => this.handleChange(e)}
                            /></td>
                      </tr>
                      <tr>
                        <td align="center" valign="top">
                            <input 
                                id="pass" 
                                name="pass" 
                                type="password" 
                                className="login_tf" 
                                maxLength="16"
                                value={this.state.name}
                                onChange={e => this.handleChange(e)}
                            /></td>
                      </tr>
                      <tr>
                        <td align="center" valign="top">
                            <a onClick={this.loginClick} className="login_button">Login</a>
                        </td>
                      </tr>
                      </tbody>
                    </table>
              </form>
            </div>
        <style jsx global>{`
            html {
                height: 100%;
                font-family:Gotham, "Helvetica Neue", Helvetica, Arial, sans-serif;
                font-size:15px;
                line-height:17px;
                color:#333;
            }
            body{
                margin:0px;
                padding:0px;
                width:100%; 
                height:100%;
            }
        `}</style>
        <style jsx>{`
            .container{
                height:100%;
            }
            .header{
                height:28px;
                padding:8px;
                background-color:#006; 
                color:#FFF;
                line-height:28px;
                font-weight:bold;
            }
            .content_box{
                height:calc(100% - 60px);
                padding:8px;
            }
            .login_button{
                padding:4px 12px 4px 12px;
                background-color:#006; 
                color:#FFF;
                border-radius:8px;
                cursor:pointer;
            }
            .login_tf{
                font-size:15px;
                padding:4px 12px 4px 12px;
                text-align:center;
                border-radius:8px;
            }
        `}</style>
          </div>
      );
    }
}

export default Admin
