import { Component } from 'react';
import Link from 'next/link';
import jsCookie from 'js-cookie';
import qs from 'querystring';

class UserNewsDetails extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id : 0,
            title : '',
            cover_image : '',
            description : '',
            publish_date : ''
        };
        let view_id = jsCookie.get('view_news_id');
        if(typeof(view_id) != 'undefined'){
            this.state.id = view_id;
        }
    };

    getContent = () => {
        var inst = this;
        (
            async function(){
                //try {
                    var goid = 0;
                    if(typeof(inst.state.id) != 'undefined' && inst.state.id != 0){
                        goid = inst.state.id;
                        console.log(goid);
                        const response = await window.fetch('/news?id=' + goid , {
                            method: 'get',
                            headers: {'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'}
                        })

                        if (response.ok) {
                            const { output } = await response.json();
                            console.log(output);
                            if(output.status){
                                var newdate = output.publish_date.toString().replace(/T/, ' ').replace(/\..+/, '');
                                inst.setState({ 
                                    title : output.title, 
                                    cover_image : output.cover_image,
                                    description : output.description,
                                    publish_date : newdate
                                });
                            }else{
                                console.log('Get News failed.');
                            }
                        } else {
                            console.log('Get News failed.');
                        }
                    } else {
                        console.log('No News ID.');
                    }
                //} catch (error) {
                    //console.log('Implementation or Network error.');
                //}
            }
        )(this);
    }

    getImage = () => {
        if(this.state.cover_image != ''){
            return (
                <img src={this.state.cover_image} className="item_image"/>
            )     
        }else{
            return ''; 
        }
    }

    componentDidMount() {
        this.getContent();
    }

    render() {
      return (
            <div className="content_box">
                <div className="content_page">
                    {this.getImage()}
                    <div className="item_title">{this.state.title}</div>
                    <div className="item_date">{this.state.publish_date}</div>
                    <div className="item_desc">{this.state.description}</div>
                </div>
                <style jsx>{`
                    .content_box{
                        height:calc(100% - 44px);
                        overflow-x:hidden;
                        overflow-y:auto;
                    }
                    .content_page{
                        padding:8px;
                    }
                    .clear{
                        clear:both;
                    }
                    .item_image{
                        width:100%; 
                        height:auto;
                        margin:8px; 
                    }
                    .item_title{
                        font-size:17px;
                        font-weight:bold; 
                        line-height:22px;
                    }
                    .item_desc{
                        font-size:15px;
                        line-height:20px;
                    }
                    .item_date{
                        height:16px;
                        font-size:12px;
                        line-height:16px;
                        color:#999999; 
                        margin-top:8px;
                    }
                `}</style>
            </div>
      );
    }
}

export default UserNewsDetails
