import { Component } from 'react'
import Link from 'next/link'

class News extends Component {

    constructor(props) {
        super(props);
        this.state = {
            is_insert: false,
            filter_date: ''
        };
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    addClick = () => {

    }

    getContent = () =>{
        if(this.state.is_insert){

        }else{
            return(
            <div className="content_box">
                <div className="table_header">
                    <div className="filter_box">
                      <select className="myselect" name="date_filter">
                        <option value="0">All</option>
                      </select>
                    </div>
                    <a onClick={this.loginClick} className="add_but">+ Add News</a>
                    <div className="clear"></div>
                </div>
                <div className="clear"></div>
                <style jsx>{`
                    .content_box{
                        height:calc(100% - 60px);
                        padding:8px;
                    }
                    .add_but{
                        float:right;
                        padding:4px 12px 4px 12px;
                        background-color:#006; 
                        color:#FFF;
                        border-radius:8px;
                        cursor:pointer;
                        margin-left:12px;
                    }
                    .table_header{
                        float:left;
                    }
                    .filter_box{
                        float:left;
                    }
                    .myselect{
                        font-size:15px;
                        border:2px solid #d3d3d5;
                        background-color:#f3f3f5; 
                        border-radius:0px; 
                        -webkit-border-radius:0px; 
                        appearance:none; 
                        -webkit-appearance: none; 
                        outline:none;
                        position: relative;
                        text-align:left;
                        z-index: 99;
                        line-height:20px;
                        padding:0px 36px 0px 8px;
                        background:url(/static/images/select_arrow.png) no-repeat right #fff;
                    }
                `}</style>
            </div>
            )
        }
    }

    render () {
      return (
          <div className="container">
            <div className="header">Admin > News<div className="logout_but">Log out</div></div>
            <div className="clear"></div>
                {this.getContent()}
        <style jsx global>{`
            html {
                height: 100%;
                font-family:Gotham, "Helvetica Neue", Helvetica, Arial, sans-serif;
                font-size:15px;
                line-height:17px;
                color:#333;
            }
            body{
                margin:0px;
                padding:0px;
                width:100%; 
                height:100%;
            }
            .clear{
                clear:both;
            }
        `}</style>
        <style jsx>{`
            .container{
                height:100%;
            }
            .header{
                height:28px;
                padding:8px;
                background-color:#006; 
                color:#FFF;
                line-height:28px;
                font-weight:bold;
            }
            .content_box{
                height:calc(100% - 60px);
                padding:8px;
            }
            .login_button{
                padding:4px 12px 4px 12px;
                background-color:#006; 
                color:#FFF;
                border-radius:8px;
                cursor:pointer;
            }
            .login_tf{
                font-size:15px;
                padding:4px 12px 4px 12px;
                text-align:center;
                border-radius:8px;
            }
            .logout_but{
                float:right;
                padding:0px 12px 0px 12px;
                color:#FFF;
                cursor:pointer;
                font-weight:normal;
            }
            .add_but{
                float:right;
                padding:4px 12px 4px 12px;
                background-color:#006; 
                color:#FFF;
                border-radius:8px;
                cursor:pointer;
                margin-left:12px;
            }
            .table_header{
                float:left;
            }
            .filter_box{
                float:left;
            }
            .myselect{
                font-size:15px;
                border:2px solid #d3d3d5;
                background-color:#f3f3f5; 
                border-radius:0px; 
                -webkit-border-radius:0px; 
                appearance:none; 
                -webkit-appearance: none; 
                outline:none;
                position: relative;
                text-align:left;
                z-index: 99;
                line-height:20px;
                padding:0px 36px 0px 8px;
                background:url(/static/images/select_arrow.png) no-repeat right #fff;
            }
        `}</style>
          </div>
      );
    }
}

export default News
