import { Component } from 'react';
import Link from 'next/link';
import jsCookie from 'js-cookie';
import qs from 'querystring';

class NewsList extends Component {

    constructor(props) {
        super(props);
        this.state = {
        };
        this.items = [];
        this.dates = ['All'];
        this.cookie = jsCookie;
    };

    getList = () => {
        (
            async function(){
                //try {
                    var date_filter = jsCookie.get('date_filter');
                    var input = {};
                    input.date_filter = date_filter;
                            console.log(input);
                    const response = await window.fetch('/news', {
                        method: 'get',
                        headers: {'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'},
                        body: qs.stringify(input)
                    })

                    if (response.ok) {
                        const { output } = await response.json();
                        if(output.status){
                            this.items = output.result;
                            this.dates = ['All'];
                            if(date_filter == 0 && this.items.length > 0){
                                for(var i = 0; i < this.items.length; i++){
                                    var item = this.items[i];
                                    var item_dt = item.publish_date.split(' ');
                                    if(this.dates.indexOf(item_dt[0]) == -1){
                                        this.dates.push(item_dt[0]);
                                    }
                                }
                            }
                            console.log(this.date);
                        }else{
                            console.log('List News failed.');
                        }
                    } else {
                        console.log('List News failed.');
                    }
                //} catch (error) {
                    //console.log('Implementation or Network error.');
                //}
            }
        )(this);
    }

    handleChange = (e) => {
        if(e.target.name == 'date_filter'){
            jsCookie.set('date_filter', e.target.value);
            window.location.href = '/admin';
        }
    };

    addNewsClick = () => {
        jsCookie.set('action', 'add_news');
        window.location.href = '/admin';
    };

    componentDidMount() {
        this.getList();
    }

    render() {
      return (
            <div className="content_box">
                <div className="table_header">
                    <div className="filter_box">
                      <select className="myselect" name="date_filter" onChange={e => this.handleChange(e)}>
                        {this.dates && this.dates.length > 0 && this.dates.map((k, v) => {
                            if(k != 'All'){
                                return <option value="{k}">{k}</option>
                            }else{
                                return <option value="0">All</option>
                            }
                        })}
                      </select>
                    </div>
                    <div onClick={this.addNewsClick} className="add_but">+ Add News</div>
                    <div className="clear"></div>
                </div>
                <div className="clear"></div>
                <style jsx>{`
                    .content_box{
                        height:calc(100% - 60px);
                        padding:8px;
                    }
                    .add_but{
                        float:right;
                        padding:4px 12px 4px 12px;
                        background-color:#006; 
                        color:#FFF;
                        border-radius:8px;
                        cursor:pointer;
                        margin-left:12px;
                    }
                    .table_header{
                        float:left;
                    }
                    .filter_box{
                        float:left;
                    }
                    .myselect{
                        font-size:15px;
                        border:2px solid #d3d3d5;
                        background-color:#f3f3f5; 
                        border-radius:0px; 
                        -webkit-border-radius:0px; 
                        appearance:none; 
                        -webkit-appearance: none; 
                        outline:none;
                        position: relative;
                        text-align:left;
                        z-index: 99;
                        line-height:20px;
                        padding:0px 36px 0px 8px;
                        background:url(/static/images/select_arrow.png) no-repeat right #fff;
                    }
                `}</style>
            </div>
      );
    }
}

export default NewsList
